import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css";



class RestaurantLanding extends Component {
    constructor(props){
      super(props)
      this.state = {
        file: null
      }
      this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
      this.setState({
        file: URL.createObjectURL(event.target.files[0])
      })
    }

    // JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]
    // JSON.parse(localStorage.getItem("user"))[3]["location"]

    render(){
    return (

      <div>
      <div className="row">
       <div className="container-fluid col-lg-12">
        <h1 className="heading-home home">Uber <span className="heading-eats">Eats</span></h1>
      </div>
      <div className="col-lg-12 col-md-12">
        <input type="file"
        name="Restaurant Image"
        onChange={this.handleChange}
        />
        <img className="restaurantLanding-img" src={this.state.file}
        alt="img-preview"
        />
        <form onSubmit={this.handleSubmit}>
        <input
          value={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
          type="text"
          name="restaurantName"
          />
          <input
          value={this.value}
          type="text"
          name="resLocation"
          />
          <input
          value={this.value}
          type="text"
          name="resDescription"
          />
          <input
          value={this.value}
          type="text"
          name="resContact"
          />
          <input
          value={this.value}
          type="file"
          name="resImage"
          />
          <button type="button">Submit</button>
        </form>
      </div>
     </div>
     </div>
      )
    }
  }

export default RestaurantLanding;
