import React,{useState} from 'react'
import Axios from "axios"
import image from "./addtocart.jpeg"
// import "./Modal.css"

export const Modal = ({showModal,setShowModal}) => {

  const [cartItems,setCartItems]=useState([]);
  const customerName = JSON.stringify(localStorage.getItem("user"[0].name));
  const customerEmail=JSON.stringify(localStorage.getItem("user"[0].email_id));
  const dishName=JSON.stringify(localStorage.getItem("myDish".dish_name));
  const restaurantName =JSON.stringify(localStorage.getItem("myDish".res_name));
  const price=JSON.stringify(localStorage.getItem("myDish".dish_price));
  const dishCategory=JSON.stringify(localStorage.getItem("myDish".dish_category));
    const CartItems = () => {
      Axios.post("http://localhost:3001/AddtoCart",{
        customerName:customerName,
        customerEmail:customerEmail,
        dishName:dishName,
        restaurantName:restaurantName,
        price:price,
        dishCategory:dishCategory
      })
    }



    return(
      <>
      {showModal ? (
        <div className="modal-all" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div showModal={showModal}>
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">Ready to Order?</h5>
              <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
                <img src={image}
                alt="add-to-cart" />
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-danger" style={{borderRadius:"50px"}} data-dismiss="modal-all" onClick={()=>setShowModal(prev=> !prev)}>Close</button>
              <button type="button" className="btn btn-success" style={{borderRadius:"50px"}} onClick={CartItems}>Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    </div>
      ) : null}
      </>
    )
  };


