import React,{useState,useMemo} from "react";
import Axios from "axios"
import Navbar from "./Navbar.jsx";
import Select from 'react-select'
import countryList from 'react-select-country-list'
import CustomerProfile from "./CustomerProfile"

function EditCustomerProfile() {


  const[customerName,setCustomerName]= useState("");
  const [dob,setDob] = useState("");
  const [city,setCity] = useState("");
  const [country,setCountry] = useState("");
  const [nickName,setNickName] = useState("");
  const [email,setEmail]=useState("");
  const [phone,setPhone]=useState("");

  const changeHandler = (e) =>{
    setCountry(country);
  }

  const options = useMemo(() => countryList().getData(), [])

  const editProfile = () =>{
    Axios.put("http://localhost:3001/EditCustomerProfile",{
      customerName:customerName,
      dob:dob,
      city:city,
      country:country,
      nickName:nickName,
      email:email,
      phone:phone
    })
  }

  return (
    <div className="card">
    <Navbar />
    <div className="card-body">
    <input
     type="text"
     name="customerName"
     placeholder="Name"
     defaultValue={JSON.parse(localStorage.getItem("user"))[0]["name"]}
     onChange = {
       (e) => {
         setCustomerName(e.target.value);
       }
     }
    />
    </div>
    <div className="card-body">
    <input
    type="date"
    min="mm-dd-yyy"
    value={CustomerProfile.dob}
    onChange = {
      (e) => {
        setDob(e.target.value);
      }
    }
     />
    </div>
    <div className="card-body">
    <input
     type="text"
    className = "form-control"
     name="city"
     placeholder="City"
     onChange = {
       (e) => {
         setCity(e.target.value);
       }
     }
    />
    </div>
    <div className="card-body">
    <Select
    options={options}
    value={country}
    onChange={changeHandler}
    />
    </div>
    <div className="card-body">
    <input
    type="text"
    className = "form-control"
    name="email"
    placeholder="Nick Name"
    onChange= { e => {
      setNickName(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    className = "form-control"
    type="text"
    placeholder="E-Mail"
    name="email"
    onChange = {
      (e) => {
        setEmail(e.target.value);
      }
    }
     />
    </div>
    <div className="card-body">
    <input
    className = "form-control"
    placeholder="Phone Number"
    type="text"
    name="phone"
    onChange = {
      (e) => {
        setPhone(e.target.value);
      }
    }
     />
    </div>
    <button type="button" class="btn btn-outline-light" onClick={editProfile}>Save Changes</button>
  </div>
  )
}

export default EditCustomerProfile;
