import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import ContentEditable from "react-contenteditable";
import { Table, Button } from 'semantic-ui-react';

class RestaurantLanding extends Component {
    constructor(props){
      super(props)
      this.state = {
        file: null
      }
      this.handleChange = this.handleChange.bind(this)
    }

    intialState={
      restaurant: [
      { id:1, restaurantName:"McDonalds", restaurantLocation:"4260 Nobel Dr, San Diego, CA 92122",
        restaurantDescription:"Satisfy all your cravings! One stop solution to your hunger.",
        restaurantContactInfo: 8584523014,
        restaurantTimings:"Open 24 hours"
      },
    ],
      row:{
        restaurantName:"",
        restaurantLocation:"",
        restaurantDescription:"",
        restaurantContactInfo:"",
        restaurantTimings:""
      },
    }

    state=this.initialState;
    handleChange(event) {
      this.setState({
        file: URL.createObjectURL(event.target.files[0])
      })
    }
    addRow = () => {
    this.setState(({ row, store }) => {
      return {
        store: [...store, { ...row, id: store.length + 1 }],
        row: this.initialState.row,
      }
      })
    }
      deleteRow = (id) => {
        this.setState(({ store }) => ({
          store: store.filter((item) => id !== item.id),
        }))
      }
      handleContentEditable = (event) => {
        const { row } = this.state
        const {
          currentTarget: {
            dataset: { column },
          },
          target: { value },
        } = event

        this.setState({ row: { ...row, [column]: value } })
      }

  render() {
    const {
      store,
      row: { restaurantName,restaurantLocation,restaurantDescription,restaurantContactInfo,restaurantTimings},
    } = this.state

    return (
      <div className="row">
      <div>
        <h1 className="heading-home home">Uber <span className="heading-eats">Eats</span></h1>
        <input type="file" onChange={this.handleChange}/>
        <img className="restaurantLanding-img" src={this.state.file}
        alt="img-preview"
        />
        </div>
        <Table celled>
        <Table.Header>
        <Table.Row>
        <Table.HeaderCell>restaurantName</Table.HeaderCell>
        <Table.HeaderCell>restaurantLocation</Table.HeaderCell>
        <Table.HeaderCell>restaurantDescription</Table.HeaderCell>
        <Table.HeaderCell>restaurantContactInfo</Table.HeaderCell>
        <Table.HeaderCell>restaurantTimings</Table.HeaderCell>
        </Table.Row>
        </Table.Header>
        <Table.Body>
        {store.map((row) => {
        return (
        <Table.Row key={row.id}>
        <Table.Cell>{row.restaurantName}</Table.Cell>
        <Table.Cell>{row.restaurantLocation}</Table.Cell>
        <Table.Cell>{row.restaurantDescription}</Table.Cell>
        <Table.Cell>{row.restaurantContactInfo}</Table.Cell>
        <Table.Cell>{row.restaurantTimings}</Table.Cell>
        <Table.Cell className="narrow">
        <Button
            onClick={() => {
                this.deleteRow(row.id)
              }}
            >
        Delete
        </Button>
        </Table.Cell>
        </Table.Row>
        )
        })}
        <Table.Row>
        <Table.Cell className="narrow">
        <ContentEditable
            html={restaurantName}
            data-column="restaurantName"
            className="content-editable"
            onChange={this.handleContentEditable}
        />
        </Table.Cell>
        <Table.Cell className="narrow">
          <ContentEditable
              html={restaurantLocation}
              data-column="restaurantLocation"
              className="content-editable"
              onChange={this.handleContentEditable}
          />
          </Table.Cell>
          <Table.Cell className="narrow">
          <ContentEditable
              html={restaurantDescription}
              data-column="restaurantDescription"
              className="content-editable"
              onChange={this.handleContentEditable}
          />
          </Table.Cell>
          <Table.Cell className="narrow">
            <ContentEditable
                html={restaurantContactInfo}
                data-column="restaurantContactInfo"
                className="content-editable"
                onChange={this.handleContentEditable}
            />
            </Table.Cell>
            <Table.Cell className="narrow">
            <ContentEditable
                html={restaurantTimings}
                data-column="restaurantTimings"
                className="content-editable"
                onChange={this.handleContentEditable}
            />
           </Table.Cell>
           <Table.Cell className="narrow">
           <Button onClick={this.addRow}>Add</Button>
           </Table.Cell>
           </Table.Row>
           </Table.Body>
           </Table>
           </div>
        )
    }
  }

export default RestaurantLanding;
