import React from 'react'
import {Card} from "react-bootstrap";



function Dish({dish}){

  // {`${dish.dish_img}`}
  const base64String = btoa([].reduce.call(new Uint8Array(dish.dish_img),function(p,c){return p+String.fromCharCode(c)},''));
  // function arrayBufferToBase64 (buffer){
  //   let binary = '';
  //   let bytes = new Uint8Array(buffer);
  //   let len = bytes.byteLength;
  //   for (let i = 0; i < len; i++) {
  //     binary += String.fromCharCode(bytes[i]);
  //   }
  //   return window.btoa(binary);
  // };

  return(
    <>
      <Card className="my-3 p-3 rounded h-90">
        <Card.Img style={{objectFit :"contain"}} variant="top" src={{uri:'data:image/jpeg;base64,' + this.arrayBufferToBase64(dish.dish_img.data)}} />
        <Card.Body>
          <Card.Title>
          {`${dish.dish_name}`}
          </Card.Title>
          <Card.Subtitle className="mb-2 text-muted">{`${dish.dish_ingredients}`}</Card.Subtitle>
          <Card.Text className="my-3">
            <div>
            <strong>{`${dish.dish_price}`}</strong>
            </div>
            </Card.Text>
            <Card.Text>
            <div>
            <strong>{`${dish.dish_description}`}</strong>
            </div>
            </Card.Text>
            <Card.Text>
            <div>
            <strong>{`${dish.dish_category}`}</strong>
            </div>
          </Card.Text>
          <button type="button" className="btn btn-success" style={{margin:"10px"}}>Add</button>

          <button type="button" className="btn btn-success" style={{margin:"10px"}}>Delete</button>
          </Card.Body>
      </Card>
    </>
  )

}


export default Dish;
