import React from "react"
import Axios from "axios"


function Location () {

  const name = JSON.parse(localStorage["user"])[0].name;
      const [location,setLocation]=useState("")

  const addAddress = () => {
    Axios.put("http://localhost:3001/AddtoCart",{
      location:location,
      name:name
    }).then(response =>{
      console.log("Added to DB!");
    }).catch(err =>{
      console.log(err);
    })
  }

    <Row>
    <Col>
      <div class="input-group mb-6">
        <select class="custom-select" id="inputGroupSelect02" onClick={(e) =>setLocation(e.target.value) }>
        <option selected>Do you want to chose an address from below or add a new one?</option>
        <option value="1">{}</option>
        <option value="2">{}</option>
        <option value="3">{}</option>
        </select>
      <div class="input-group-append">
      <label class="input-group-text" for="inputGroupSelect02">Options</label>
      </div>
      </div>
      </Col>
      <Col>
        <button type="button" className="btn btn-success" style={{borderRadius:"50px"}} onClick={addAddress}>Add</button>
    </Col>
    </Row>

}

export default Location;
