import React,{useState,useEffect} from 'react';
import Axios from "axios";
import "./Filter.css";
import {Col} from "react-bootstrap";
import Restaurant from "../Customer/Restaurant.js"



function Filter() {


  const [restaurants,getRestuarants]=useState([]);

  const[filterValDelivery1,setFilterValDelivery1]= useState("");
  const[filterValDelivery2,setFilterValDelivery2]= useState("");

  useEffect(() => {
    getAllRestaurants();
  },[]);

  const getAllRestaurants = () =>{
    Axios.get("http://localhost:3001/RestaurantDisplay")
    .then((response) => {
      const AllRestuarntData=response.data.details;
      getRestuarants(AllRestuarntData);
    })
    .catch(error =>
      console.error(`Error:{error}`));
  }


    return (
        <div>
        <div className="filter-body">
        <link href="https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/css/font-awesome.min.css" rel="stylesheet" />
        <div className="container">
        <div className="row">

          <div className="col-md-8">
            <h2 className="grid-title"><i className="fa fa-filter"></i> Filters</h2>
            <hr />

            <h4>Type of delivery:</h4>
            <div class="checkbox">
              <label><input type="checkbox" className="icheck" value="delivery" id="check1"
              onChange={
                (e) => {
                  setFilterValDelivery1(e.target.value)
                }
                }
              />
              Delivery
              </label>
            </div>
            <div className="checkbox1">
              <label><input type="checkbox" className="icheck" value="takeAway" id="check2"
              onChange={
                (e) => {
                  setFilterValDelivery2(e.target.value)
                }
                }
              />
              Take Away
              </label>
            </div>
            <div className="padding">
            </div>


              <h4>Type of Food:</h4>
            <div className="checkbox2">
              <label><input type="checkbox" className="icheck" /> Vegan</label>
            </div>
            <div className="checkbox">
              <label><input type="checkbox" className="icheck" /> Veg</label>
            </div>
            <div class="checkbox">
              <label><input type="checkbox" className="icheck" /> Non Veg</label>
            </div>

            <div className="padding">
            </div>
        </div>
        </div>
        </div>
         </div>
       {restaurants.filter(function(restaurant) {
           if(filterValDelivery1.checked==="" && filterValDelivery2.checked===""){
             return false;
           }
           else if(filterValDelivery1.checked=="true" && ((restaurant.type_of_delivery.toLowerCase()).includes("Delivery".toLowerCase()))) {
           return true;
          }}).map(function(restaurant){
           return(<Col sm={12} md={6} lg={4} key={restaurant.id}>
             <h3>Filter Results</h3>
             <Restaurant restaurant={restaurant} />
             </Col>
              )
            })
          }

     </div>
    )
}



export default Filter;
