import React,{useState,useEffect, useCallback } from 'react'
import Axios from "axios";
import  "../AddtoCart/AddtoCart.css";





function AddtoCartItem({cart}) {


  return(
    <>
    <div className="card dishes" style={{width: "300px"}}>
    <img src={`data:image/jpeg;base64,${cart.dish_img}`} className="card-img-top" alt="dish-img" />
    <div className="card-body">
      <h5 className="card-title">{`${cart.dish_name}`}</h5>
      <h5 className="card-text">{`${cart.dish_ingredients}`}</h5>
      <h5 className="card-text">{`${cart.dish_price}`}</h5>
      <h5 className="card-text">{`${cart.dish_description}`}</h5>
      <h5 className="card-text">{`${cart.dish_category}`}</h5>
      <button type="button" className="btn btn-success" style={{borderRadius:"50px"}}>Go to checkout</button>
      </div>
      </div>
      </>
  );
}



export default AddtoCartItem;
