import React, {useState} from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import Axios from "axios";


function Form () {

  const [resName,setResName] = useState("");
  const [resLocation,setResLocation] = useState("");
  const [resImg,setResImg] = useState("");
  const [resDescription,setResDescription] = useState("");
  const [resContact,setResContact] = useState("");
  const [resTimings,setResTimings] = useState("");

  function submitForm () {
    function submitRestaurantForm() {
      Axios.post("http://localhost:3001/RestaurantLanding.jsx",
      {
        resName:resName,
        resLocation:resLocation,
        resImg:resImg,
        resDescription:resDescription,
        resContact:resContact,
        resTimings:resTimings
      })
      // }).then((response) => {
      //   if(response.data.message){
      //     setCustomerSatus(response.data.message)
      //   }
      //   else{
      //     setCustomerSatus("");
      //   }
      // });
    };

  }
  function showInput() {
    document.getElementById('name').innerHTML = document.getElementById("res_name").value;
    document.getElementById('location').innerHTML = document.getElementById("res_location").value;
    document.getElementById('description').innerHTML = document.getElementById("res_description").value;
    document.getElementById('contact').innerHTML = document.getElementById("res_contact").value;
    document.getElementById('timings').innerHTML = document.getElementById("res_timings").value;
  }

  return(
    <div className="card">
    <div className="card-body">
    <input
     id="res_name"
     type="text"
     name="resName"
     placeholder="Restaurant Name"
     defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
     onChange = {
       (e) => {
         setResName(e.target.value);
       }
     }
    />

    </div>
    <div className="card-body">
    <input
    id="res_location"
     type="text"
    className = "form-control"
     name="resLocation"
     placeholder="Restaurant Location"
     defaultValue={JSON.parse(localStorage.getItem("user"))[0]["location"]}
     onChange = {
       (e) => {
         setResLocation(e.target.value);
       }
     }
    />
    </div>
    <div className="card-body file">
    <input
    id="res_img"
    type="file"
   className = "form-control"
    name="resImg"
    onChange= { e => {
      setResImg(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    id="res_description"
    type="text"
    className = "form-control"
    name="resDescription"
    placeholder="Restaurant Description"
    onChange= { e => {
      setResDescription(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    id="res_contact"
    type="text"
      className = "form-control"
    name="resContact"
    placeholder="Restaurant Contact"
    onChange= { e => {
      setResContact(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    id="res_timings"
    type="text"
      className = "form-control"
    name="resTimings"
    placeholder="Restaurant Timings"
    onChange= { e => {
      setResTimings(e.target.value);
    }}
    />
    <button type="button" class="btn btn-outline-info" onClick={showInput}>Save Changes</button>
    <p><span id="name"></span></p>
    <p><span id="location"></span></p>
      <p><span id="image"></span></p>
    <p><span id="description"></span></p>
    <p><span id="contact"></span></p>
    <p><span id="timings"></span></p>
    </div>
    </div>
  );

}


export default Form;
