import React,{useState,useEffect,Fragment} from "react";
import Axios from "axios";
import {Row,Col} from "react-bootstrap";
import Restaurant from "./Restaurant.js"
import RestaurantsNearYou from "./RestaurantsNearYou.js"
import "./Searchbar.css";


function RestaurantDisplay(){
    const [restaurants,getRestuarants]=useState([]);

    useEffect(() => {
      getAllRestaurants();
    },[]);

  const getAllRestaurants = () =>{
    Axios.get("http://localhost:3001/RestaurantDisplay")
    .then((response) => {
      const AllRestuarntData=response.data.details;
      getRestuarants(AllRestuarntData);
    })
    .catch(error =>
      console.error(`Error:{error}`));
  }

  // var resNear = restaurants.filter(function(restaurant) {
  //   if (!(restaurant.location).includes(JSON.parse(localStorage.getItem("user"))[0]["city"])) {
  //     return false; // skip
  //   }
  //   return true;
  // }).map(function(restaurant) { });


  return(
    <Fragment>
    <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.0-alpha1/dist/js/bootstrap.bundle.min.js" />
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.2.1/jquery.min.js" />
    <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.0-alpha1/dist/css/bootstrap.min.css" />
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css" />
    <div className="container">
    <div className="d-flex justify-content-right align-items-right">
            <div className="col-md-6" style={{color:"#F3F0D7"}}>
                <div className="form">
                <i className="fa fa-search"></i>
                <input type="text" className="form-control form-input" placeholder="What are your craving for?..." />
                </div>
            </div>
        </div>
    </div>
      <Row>
      <h3 style={{padding:"3%"}}>Restaurants Near You:</h3>
      {restaurants.filter(function(restaurant) {
        if (!(restaurant.location).includes(JSON.parse(localStorage.getItem("user"))[0]["city"])) {
          return false;
        }
        return true;
      }).map(function(restaurant){
        return(<Col sm={8} md={4} lg={3} key={restaurant.id}>
          <Restaurant restaurant={restaurant} />
        </Col>
      )
      })}
      <h3 style={{padding:"3%"}}>Other Restaurants Open:</h3>
      {restaurants.filter(function(restaurant) {
        if ((restaurant.location).includes(JSON.parse(localStorage.getItem("user"))[0]["city"])) {
          return false;
        }
        return true;
        }).map(function(restaurant){
          return(
        <Col sm={8} md={4} lg={3} key={restaurant.id}>
          <RestaurantsNearYou restaurant={restaurant} />
        </Col>
      )
    })}
      </Row>
    </Fragment>
  );
}

export default RestaurantDisplay;
