import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

var list = JSON.parse(localStorage.getItem("user"));

class RestaurantLanding extends Component {
    constructor(props){
      super(props)
      this.state = {
        file: null
      }

      this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
      this.setState({
        file: URL.createObjectURL(event.target.files[0])
      })
    }



    render(){
    return (

      <div>
      <div className="row">
       <div className="container-fluid col-lg-12">
        <h1 className="heading-home home">Uber <span className="heading-eats">Eats</span></h1>
      </div>
      <div className="col-lg-12 col-md-12">
        <input type="file"
        name="Restaurant Image"
        onChange={this.handleChange}
        />
        <img className="restaurantLanding-img" src={this.state.file}
        alt="img-preview"
        />
        <div className="card mb-3">
        <div className="card-body">
          <h5 className="card-title">{list}</h5>
          <p className="card-text">{this.props.Location}</p>
          <p className="card-text"><small className="text-muted">Last updated 3 mins ago</small></p>
        </div>
       </div>
      </div>
      </div>
     </div>
      )
    }
  }

export default RestaurantLanding;
