import React from 'react'
import image from "./addtocart.jpeg"
import "./Modal.css"

export const Modal = ({showModal,setShowModal}) => {
    return(
      <>
      {showModal ? (
        <div showModal={showModal}>
        <div className="modal body-modal">
            <img src={image}
            alt="add-to-cart" />
            <div>
                <h3>Ready to Order?</h3>
                <button type="button" className="btn btn-success" style={{borderRadius:"50px"}}>Add Item</button>
            </div>
            <div aria-label="Close Modal" onClick={()=>setShowModal(prev=> !prev)} />
            </div>
          </div>
      ) : null}
      </>
    )
  };
