import React,{useState} from 'react';
import NavBar from "./NavBar.jsx";
import Axios from "axios";


function Edit(){

  // const [resLocation,setResLocation] = useState("");
  // const [resImg,setResImg] = useState("");
  // const [resDescription,setResDescription] = useState("");
  // const [resContact,setResContact] = useState("");
  // const [resTimings,setResTimings] = useState("");

  const[newName,setNewName]= useState("");
  const [newLocation,setNewLocation] = useState("");
  const [newImg,setNewImg] = useState("");
  const [newDescription,setNewDescription] = useState("");
  const [newContact,setNewContact] = useState("");
  const [newTimings,setNewTimings] = useState("");

  function editRestaurant() {
      Axios.put("http://localhost:3001/Edit",
      {
        resName:resName,
        newLocation:newLocation,
        newImg:newImg,
        newDescription:newDescription,
        newContact:newContact,
        newTimings:setNewTimings
      })
    }


  return(
    <>
    <div className="card">
    <NavBar />
    <h2>{JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}</h2>
    />
      <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}}>✔️</button>
    </div>
    <div className="card-body">
    <input
    id="res_location"
     type="text"
    className = "form-control"
     name="resLocation"
     placeholder="Restaurant Location"
     defaultValue={JSON.parse(localStorage.getItem("user"))[0]["location"]}
     onChange = {
       (e) => {
         setNewLocation(e.target.value);
       }
     }
    />
    </div>
    <div className="card-body file">
    <input
    id="res_img"
    type="file"
   className = "form-control"
    name="resImg"
    onChange= { e => {
      setNewImg(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    id="res_description"
    type="text"
    className = "form-control"
    name="resDescription"
    placeholder="Restaurant Description"
    onChange= { e => {
      setNewDescription(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    id="res_contact"
    type="text"
      className = "form-control"
    name="resContact"
    placeholder="Restaurant Contact"
    onChange= { e => {
      setNewContact(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    id="res_timings"
    type="text"
      className = "form-control"
    name="resTimings"
    placeholder="Restaurant Timings"
    onChange= { e => {
      setNewTimings(e.target.value);
    }}
    />
    <button type="button" class="btn btn-outline-light" onClick={editRestaurant}>Save Changes</button>
    </div>
    </>

  );

}

export default Edit;
