import React from 'react'
import {Link} from "react-router-dom";



function Dish({dish}){

  const EditDishes = ()=>{
    localStorage.setItem("val",dish.dish_name);
  }

 // ((JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]) === (dish.res_name))

  return(
    <div>
      <div className="card mb-3" style={{width: "25rem",maxWidth: "540px"}}>
      <img src={dish.dish_img} className="img-fluid rounded-start" alt="dish-img" />
      </div>
      <div className="col-md-8">
      <div className="card-body">
        <h5 className="card-title">{`${dish.dish_name}`}</h5>
        <p className="card-text">{`${dish.dish_ingredients}`}</p>
        <p className="card-text">{`${dish.dish_description}`}</p>
        <p className="card-text">{`${dish.dish_category}`}</p>
        <p className="card-text"><small className="text-muted">{`${dish.dish_price}`}</small></p>
        <Link to="/EditDishes">
        <button type="button" className="btn btn-success btn-sm" style={{margin:"10px",borderRadius:"10%"}} onClick={()=>EditDishes(dish.dish_name)}>Edit</button>
        </Link>
        </div>
        </div>
        </div>

  )

  // return("")

}


export default Dish;
