import React,{useState,Fragment} from 'react';
import NavBar from "./NavBar.jsx";
import Axios from "axios";
import {Row,Col} from "react-bootstrap"


function Edit(){


  const[newName,setNewName]= useState("");
  const [newLocation,setNewLocation] = useState("");
  const [newImg,setNewImg] = useState("");
  const [newDescription,setNewDescription] = useState("");
  const [newContact,setNewContact] = useState("");
  const [newTimings,setNewTimings] = useState("");

  function editRestaurant() {
      Axios.put("http://localhost:3001/EditName",
      {
        resName:JSON.parse(localStorage.getItem("user"))[0]["restaurantName"],
        newName:newName,
        newLocation:newLocation,
        newImg:newImg,
        newDescription:newDescription,
        newContact:newContact,
        newTimings:setNewTimings
      })
    }


  return(
    <Fragment>
    <NavBar />
    <h2>{JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}</h2>
    <Row style={{margin:"100px"}}>
    <Col>
    <input
    id="res_location"
     type="text"
     name="newName"
     placeholder="Restaurant"
     className="form-control"
     defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
     onChange = {
       (e) => {
         setNewName(e.target.value);
       }
     }
    />
    </Col>
    <Col>
      <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}}>✔️</button>
      </Col>
      </Row>

      <Row style={{margin:"100px"}}>
      <Col>
      <input
       type="text"
       name="newLocation"
       placeholder="Location"
       className="form-control"
       defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
       onChange = {
         (e) => {
           setNewLocation(e.target.value);
         }
       }
      />
      </Col>
      <Col>
        <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}}>✔️</button>
        </Col>
        </Row>

        <Row style={{margin:"100px"}}>
        <Col>
        <input
         type="file"
         name="newImg"
         placeholder="Image"
         className="form-control"
         defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
         onChange = {
           (e) => {
             setNewImg(e.target.value);
           }
         }
        />
        </Col>
        <Col>
          <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}}>✔️</button>
          </Col>
          </Row>

          <Row style={{margin:"100px"}}>
          <Col>
          <input
          id="res_location"
           type="text"
           name="newDescription"
           placeholder="Description"
           className="form-control"
           onChange = {
             (e) => {
               setNewDescription(e.target.value);
             }
           }
          />
          </Col>
          <Col>
            <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}}>✔️</button>
            </Col>
            </Row>

            <Row style={{margin:"100px"}}>
            <Col>
            <input
            id="res_location"
             type="text"
             name="newContact"
             placeholder="Contact"
             className="form-control"
             defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
             onChange = {
               (e) => {
                 setNewContact(e.target.value);
               }
             }
            />
            </Col>
            <Col>
              <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}}>✔️</button>
              </Col>
              </Row>

              <Row style={{margin:"100px"}}>
              <Col>
              <input
              id="res_location"
               type="text"
               name="newTimings"
               placeholder="Timings"
               className="form-control"
               defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
               onChange = {
                 (e) => {
                   setNewTimings(e.target.value);
                 }
               }
              />
              </Col>
              <Col>
                <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}}>✔️</button>
                </Col>
                </Row>

    </Fragment>
  );

}

export default Edit;
