import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import ContentEditable from "react-contenteditable";
import { Table, Button } from 'semantic-ui-react';

class RestaurantLanding extends Component {
  constructor(props){
    super(props)
    this.state = {
      file: null
    }
    this.handleChange = this.handleChange.bind(this)
  }
  handleChange(event) {
    this.setState({
      file: URL.createObjectURL(event.target.files[0])
    })
  }
  intialState={
    restaurant: [
    { id:1, restaurantName:"McDonalds", restaurantLocation:"4260 Nobel Dr, San Diego, CA 92122",
      restaurantDescription:"Satisfy all your cravings! One stop solution to your hunger.",
      restaurantContactInfo: 8584523014,
      restaurantTimings:"Open 24 hours"
    }
  ],
    row:{
      restaurantName:"",
      restaurantLocation:"",
      restaurantDescription:"",
      restaurantContactInfo:"",
      restaurantTimings:""
    }
  }
  render() {
    return (
      <div className="row">
      <div>
        <input type="file" onChange={this.handleChange}/>
        <img className="restaurantLanding-img" src={this.state.file}
        alt="img-preview"
        />
        <div class="card text-white bg-dark mb-3" style={{width: "90rem"}}>
        <div class="card-header">Header</div>
        <div class="card-body">
        <h5 class="card-title">Primary card title</h5>
        <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
        </div>
        </div>
        </div>
        </div>
    );
  }
}

export default RestaurantLanding;
