import React,{useState} from 'react'
import {Row,Col} from "react-bootstrap";
import Axios from "axios"

function DeliveryStatus() {

const [deliveryStatus,getDeliveryStatus]=useState();

const[delivery,setDelivery]=useState(null);

const updateCartTable = () => {
  Axios.get("http://localhost:3001/AddtoCartView",{
    customerName:"Ed Sheeran",
    restaurantName:"Mc Donald's"
  }).then((response) =>{
    console.log(response.data.dashboard)
      localStorage.setItem("deliveryStatusCustomer",response.data.dashboard)
      console.log("here")
    }).catch(error =>
      console.error(`Error:{error}`));
      console.log(delivery);
    Axios.put("http://localhost:3001/AddDeliveryStatus",{
      delivery:delivery,
      customerName:localStorage.getItem("customer"),
      restaurantName:JSON.parse(localStorage["user"])[0].restaurantName
    })
  // alert("Updated successfully!");
}


    return (
      <>
        <div className="row" style={{textAlign:"center", fontFamily:"Postmates", height:"40px",lineHeight:"10px"}}>
        <Col>
        </Col>
        <Col>
        </Col>
        <Col>
        <select
        className="form-value"
        name="delivery"
        style={{textAlign:"center", fontFamily:"Postmates", height:"40px",lineHeight:"3rem", fontSize:"1.1rem", width:"200px"}}
        onClick={()=>updateCartTable(localStorage.getItem("customer"))}
        onChange={
          (e)=>{
            setDelivery(e.target.value);
          }}
        >
          <option value="select">Select</option>
          <option value="Order Received">Order Received</option>
          <option value="Preparing">Preparing</option>
          <option value="On the Way">On the Way</option>
          <option value="Delivered">Delivered</option>
          <option value="Pick Up Ready">Pick Up Ready</option>
          <option value="Picked Up">Picked Up</option>
        </select>
        </Col>
        <Col>
        </Col>
        </div>
      </>
    )
}

export default DeliveryStatus
