    </div>
    <div className="card-body">
    <input
    type="text"
      className = "form-control"
    name="resContact"
    placeholder="Restaurant Contact"
    onChange= { e => {
      setNewContact(e.target.value);
    }}
    />
    <button type="button" class="btn btn-outline-light" onClick={()=>{editContact(resName)}}>Save Changes</button>
    </div>
    <div className="card-body">
    <input
    type="text"
      className = "form-control"
    name="resTimings"
