import React,{useState} from 'react'
import Axios from "axios"
import Reciept from "./Reciept.jsx"

// import "./Modal.css"

export const ModalReciept = ({showModal,setShowModal}) => {

const [location,setLocation]=useState("")
const name = JSON.parse(localStorage["user"])[0].name;
  const addAddress = () => {
    Axios.put("http://localhost:3001/AddtoCart",{
      location:location,
      name:name
    }).then(response =>{
      console.log("Added to DB!");
    }).catch(err =>{
      console.log(err);
    })
  }

    return(
      <>
      {showModal ? (
        <div className="modal-all" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true" style={{backgroundColor:"#000000"}} style={{marginTop:"100px",textAlign:"center", fontFamily:"Postmates", height:"40px",lineHeight:"10px"}} >
        <div showModal={showModal}>
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">Order Summary</h5>
              <button type="button" className="close btn-success" data-dismiss="modal-all" aria-label="Close" style={{width:"25px",height:"25px"}} onClick={()=>setShowModal(prev=> !prev)}>
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
            <Reciept />
            </div>
          </div>
        </div>
      </div>
    </div>
      ) : null}
      </>
    )
  };
