import React,{useState} from 'react'
import Axios from "axios"
import {Row,Col} from "react-bootstrap";

  function DropdownLocation () {

    const [location,setLocation]=useState("")
    const [allValues,getAllValues]=useState("");

  const name = JSON.parse(localStorage["user"])[0].name;

  useEffect(() => {
    getAllLocation();
  },[]);


const getAllLocation = () =>{
  Axios.get("http://localhost:3001/GetLocation")
  .then((response) =>{
    if(JSON.parse(localStorage["user"])[0].name===response.data.details.customer_name){
      getAllValues=response.data.details;
    }
  })
}

    const addAddress = () => {


      Axios.put("http://localhost:3001/AddtoCart",{
        location:location,
        name:name
      }).then(response =>{
        console.log("Added to DB!");
      }).catch(err =>{
        console.log(err);
      })
    }



    return (
<>
      <Row>
      <Col>
        <div class="input-group mb-3">
        <select class="custom-select" id="inputGroupSelect02">
        <option selected>Choose...</option>
        <option value="1"></option>
        <option value="2">Two</option>
        <option value="3">Three</option>
        </select>
        <div class="input-group-append">
        <label class="input-group-text" for="inputGroupSelect02">Options</label>
        </div>
        </div>
        </Col>
        <Col>
          <button type="button" className="btn btn-success" style={{borderRadius:"50px"}} onClick={addAddress}>Add</button>
        </Col>
        </Row>
  </>


    )
}

export default DropdownLocation;
