import React,{useState} from "react";
import "./Searchbar.css";

function SearchBar() {

  const[searchTerm,setSearchTerm]=useState("");

  return(
    <div className="container-fluid" style={{marginTop:"0px"}}>
    <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/css/bootstrap.min.css" />
    <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/js/bootstrap.bundle.min.js" />
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.0.3/css/font-awesome.css" />
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.2.1/jquery.min.js" />
    <div className="container justify-content-center">
    <div className="row">
        <div className="col-md-8">
            <div className="input-group mb-3">
            <input type="text" className="form-control input-text" placeholder="What are you craving for...." aria-label="Recipient's username" aria-describedby="basic-addon2" style={{border: "1px solid #BFD8B8"}} />
                <div className="input-group-append"> <button className="btn btn-outline-success btn-lg" type="button" onClick={(e)=>{setSearchTerm(e.target.value)}}>
                <i className="fa fa-search"></i></button>
            </div>
            </div>
        </div>
    </div>
  </div>
  </div>
  )
}

export default SearchBar;
