import React,{useState} from 'react'
import Axios from "axios"
import image from "./addtocart.jpeg"
import "./Modal.css"

export const Modal = ({showModal,setShowModal}) => {

  const [cartItems,setCartItems]=useState([]);

    const CartItems = () => {
      Axios.post("http://localhost:3001/AddtoCart",{
        customerName:JSON.parse(localStorage.getItem("user"[0].name)),
        customerEmail:JSON.parse(localStorage.getItem("user"[0].email_id)),
        dishName:JSON.parse(localStorage.getItem("myDish".dish_name)),
        restaurantName:JSON.parse(localStorage.getItem("myDish".res_name)),
        price:JSON.parse(localStorage.getItem("myDish".dish_price)),
        dishCategory:JSON.parse(localStorage.getItem("myDish".dish_category))
      })
    }



    return(
      <>
      {showModal ? (
        <div className="body-modal">
        <div showModal={showModal}>
            <img src={image}
            alt="add-to-cart" />
            <div className="modal-dialog">
                <h3>Ready to Order?</h3>
                <button type="button" className="btn btn-success" style={{borderRadius:"50px"}} onClick={CartItems}>Add Item</button>
                  <button type="button" class="btn btn-dark" data-dismiss="modal" style={{borderRadius:"50px"}} onClick={()=>setShowModal(prev=> !prev)}>Close</button>
            </div>
            <div aria-label="Close Modal" onClick={()=>setShowModal(prev=> !prev)} />
            </div>
          </div>

      ) : null}
      </>
    )
  };
