import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.min.css";



class RestaurantLanding extends Component {
    constructor(props){
      super(props)
      this.state = {
        file: null
      }
      this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
      this.setState({
        file: URL.createObjectURL(event.target.files[0])
      })
    }

    // JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]
    // JSON.parse(localStorage.getItem("user"))[3]["location"]
    render(){
    return (

      <div>
      <div className="row">
       <div className="container-fluid col-lg-12">
        <h1 className="heading-home home">Uber <span className="heading-eats">Eats</span></h1>
      </div>
      <div className="col-lg-12 col-md-12">
        <input type="file"
        name="Restaurant Image"
        onChange={this.handleChange}
        />
        <img className="restaurantLanding-img" src={this.state.file}
        alt="img-preview"
        />
        <div className="card">
        <div className="card-body">
        {JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
        </div>
        <div className="card-body">
        {JSON.parse(localStorage.getItem("user"))[0]["location"]}
        </div>
        <div className="card-body file">
        <input
        type="file"
        name="resImg"
        onChange= { e => {
          setResImg(e.target.value);
        }}
        />
        </div>
        </div>
      </div>
     </div>
     </div>
      )
    }
  }

export default RestaurantLanding;
