import React, {useState} from'react';
import RestaurantLanding from './RestaurantLanding';
import Axios from 'axios';

function EditProfile(){
    const[wname,setName]=useState(" ");
    const[newname,setNewName]=useState(" ");
    const[newemail,setNewEmail]=useState(" ");
    const[newphone,setNewPhone]=useState(" ");
    const[newloc,setNewLoc]=useState(" ");
    const[newdesc,setNewDesc]=useState(" ");
    const[newtime,setNewTime]=useState(" ");
    const editName=()=>{
        console.log("Editing called");

        Axios.post("http://localhost:3001/editName",
{name:wname,newName:newname}).then((response)=>{

console.log(response.data);

});
    }

    const editEmail=()=>{
        console.log("Editing called");

        Axios.post("http://localhost:3001/editName",
{name:wname,newemail:newemail}).then((response)=>{

console.log(response.data);

});
    }

    const editPhone=()=>{
        console.log("Editing called");

        Axios.post("http://localhost:3001/editName",
{name:wname,newPhone:newphone}).then((response)=>{

console.log(response.data);

});
    }
    const editLoc=()=>{
        console.log("Editing called");

        Axios.post("http://localhost:3001/editName",
{name:wname,newLoc:newloc}).then((response)=>{

console.log(response.data);

});
    }

    const editDesc=()=>{
        console.log("Editing called");

        Axios.post("http://localhost:3001/editName",
{name:wname,newDesc:newdesc}).then((response)=>{

console.log(response.data);

});
    }
    const editTime=()=>{
        console.log("Editing called");

        Axios.post("http://localhost:3001/editName",
{name:wname,newTime:newtime}).then((response)=>{

console.log(response.data);

});
    }

    return(
        <><div class="editprofile">
            <form >
                <div className="form-group" >
                    <label>Restaurant Name:</label>
                    <input type="text" onChange={(e)=>{
                    setName(e.target.value); }}></input>
                    <button type="submit" className="btn-btn success">OK</button>
                </div>
            </form>

            <form onSubmit={editName}>
                <div className="form-group" >
                    <label>Restaurant Name:</label>
                    <input type="text" onChange={(e)=>{
                    setNewName(e.target.value); }}></input>
                    <button type="submit" className="btn-btn success">Edit</button>
                </div>
            </form>

            <form onSubmit={editEmail}>
                <div className="form-group" >
                    <label>Email:</label>
                    <input type="text" onChange={(e)=>{
                    setNewEmail(e.target.value); }}></input>
                    <button type="submit" className="btn-btn success">Edit</button>
                </div>
            </form>

            <form onSubmit={editPhone}>
                <div className="form-group" >
                    <label>Phone:</label>
                    <input type="text" onChange={(e)=>{
                    setNewPhone(e.target.value); }}></input>
                    <button type="submit" className="btn-btn success">Edit</button>
                </div>
            </form>

            <form onSubmit={editLoc}>
                <div className="form-group" >
                    <label>Location:</label>
                    <input type="text" onChange={(e)=>{
                    setNewLoc(e.target.value); }}></input>
                    <button type="submit" className="btn-btn success">Edit</button>
                </div>
            </form>

            <form onSubmit={editDesc}>
                <div className="form-group" >
                    <label>Description:</label>
                    <input type="text" onChange={(e)=>{
                    setNewDesc(e.target.value); }}></input>
                    <button type="submit" className="btn-btn success">Edit</button>
                </div>
            </form>

            <form onSubmit={editTime}>
                <div className="form-group" >
                    <label>Time:</label>
                    <input type="text" onChange={(e)=>{
                    setNewTime(e.target.value); }} placeholder="eg: 9am to 5pm"></input>
                    <button type="submit" className="btn-btn success">Edit</button>
                </div>
            </form>




        </div></>
    );
}

export default EditProfile;
