import React, {useState} from 'react';
import "bootstrap/dist/css/bootstrap.min.css";


function Form () {

  const [resName,setResName] = useState("");
  const [resLocation,setResLocation] = useState("");
  const [resImg,setResImg] = useState("");
  const [resDescription,setResDescription] = useState("");
  const [resContact,setResContact] = useState("");
  const [resTimings,setResTimings] = useState("");

  function showInput() {
    document.getElementById('name').innerHTML = document.getElementById("res_name").value;
    document.getElementById('location').innerHTML = document.getElementById("res_location").value;
    document.getElementById('img').innerHTML = document.getElementById("res_img").value;
    document.getElementById('contact').innerHTML = document.getElementById("res_description").value;
    document.getElementById('contact').innerHTML = document.getElementById("res_name").value;
    document.getElementById('timings').innerHTML = document.getElementById("res_name").value;
  }

  return(
    <div className="card">
    <div className="card-body">
    <input
     id="res_name"
     type="text"
     name="resName"
     defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
     onChange = {
       (e) => {
         setResName(e.target.value);
       }
     }
    />

    </div>
    <div className="card-body">
    <input
    id="res_location"
     type="text"
    className = "form-control"
     name="resLocation"
     defaultValue={JSON.parse(localStorage.getItem("user"))[0]["location"]}
     onChange = {
       (e) => {
         setResLocation(e.target.value);
       }
     }
    />
    </div>
    <div className="card-body file">
    <input
    id="res_img"
    type="file"
   className = "form-control"
    name="resImg"
    onChange= { e => {
      setResImg(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    id="res_description"
    type="text"
    className = "form-control"
    name="resDescription"
    onChange= { e => {
      setResDescription(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    id="res_contact"
    type="text"
      className = "form-control"
    name="resContact"
    onChange= { e => {
      setResContact(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    id="res_timings"
    type="text"
      className = "form-control"
    name="resTimings"
    onChange= { e => {
      setResTimings(e.target.value);
    }}
    />
    <button type="button" class="btn btn-outline-info" onClick={showInput}>Save Changes</button>
    <p><span id="name"></span></p>
    <p><span id="location"></span></p>
      <p><span id="image"></span></p>
    <p><span id="description"></span></p>
    <p><span id="contact"></span></p>
    <p><span id="timings"></span></p>
    </div>
    </div>
  );

}


export default Form;
