import React,{useState,useEffect} from "react";
import "./Searchbar.css";
import {Row,Col} from "react-bootstrap";
import Restaurant from "../Customer/Restaurant.js"

function SearchBar() {

    const[searchTerm,setSearchTerm]=useState("");

    useEffect(() => {
      getAllRestaurants();
    },[]);

  const getAllRestaurants = () =>{
    Axios.get("http://localhost:3001/RestaurantDisplay")
    .then((response) => {
      const AllRestuarntData=response.data.details;
      getRestuarants(AllRestuarntData);
    })
    .catch(error =>
      console.error(`Error:{error}`));
  }

    const searchList = () => {
       restaurants.filter(function(restaurant) {
      if(SearchBar.searchTerm===""){
        return false;
      }
      else if(((restaurant.location).includes(SearchBar.searchTerm)) || ((restaurant.description).includes(SearchBar.searchTerm))) {
      return true;
    }}).map(function(restaurant){
      return(<Col sm={8} md={4} lg={3} key={restaurant.id}>
        <Restaurant restaurant={restaurant} />
      </Col>
    )
    })
  }

  return(
    <div className="container-fluid" style={{marginTop:"0px"}}>
    <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/css/bootstrap.min.css" />
    <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/js/bootstrap.bundle.min.js" />
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.0.3/css/font-awesome.css" />
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.2.1/jquery.min.js" />
    <div className="container justify-content-right">
    <div className="row">
        <div className="col-md-8">
            <div className="input-group mb-3">
            <input type="text"
            className="form-control input-text"
            placeholder="What are you craving for...."
            aria-label="search for food"
            aria-describedby="basic-addon2"
            style={{border: "1px solid #BFD8B8"}}
            onChange={
              (e)=> {
                setSearchTerm(e.target.value);
              }
            }
            />
                <div className="input-group-append">
                <button className="btn btn-outline-success btn-lg" type="button" OnClick={searchList}>
                <i className="fa fa-search"></i>
                </button>
            </div>
            </div>
        </div>
    </div>
  </div>
  </div>
  )
}

export default SearchBar;
