import React,{useState} from "react"
import Axios from "axios"
import {Row,Col} from "react-bootstrap";


function Location ({loc}) {

  const name = JSON.parse(localStorage["user"])[0].name;
  const [location,setLocation]=useState("")

  const addAddress = () => {
    Axios.put("http://localhost:3001/AddtoCart",{
      location:location,
      name:name
    }).then(response =>{
      console.log("Added to DB!");
    }).catch(err =>{
      console.log(err);
    })
  }
return (
    <Row>
    <Col>
      <div className="input-group mb-6">
        <select className="custom-select" id="inputGroupSelect02" onClick={(e) =>setLocation(e.target.value) }>
        <option selected>Choose Delivery Address</option>
        <option value="1">{loc.customer_name}</option>
        <option value="2">{loc.customer_name}</option>
        <option value="3">{loc.customer_name}</option>
        </select>
      </div>
      </Col>
      <Col>
        <button type="button" className="btn btn-success" style={{borderRadius:"50px"}} onClick={addAddress}>Add</button>
    </Col>
    </Row>
  )

}

export default Location;
