import React, {Fragment,useEffect,useState} from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Axios from "axios";
import {Row,Col} from "react-bootstrap";
// import Order from "./Order.js";

function Orders() {


    const [orders,getOrders]=useState([]);

    useEffect(() => {
      getAllOrders();
    },[]);

    const getAllOrders = () => {
      // Axios.get("http://localhost:3001/Dishes.jsx")
      // .then(response => console.log(response))
      // .catch(err => console.log("failed:",err));
        Axios.get("http://localhost:3001/OrdersRestaurant")
        .then((response) => {
          const allOrders=response.data.details;
            getOrders(allOrders);
        })
        .catch(error =>
          console.error(`Error:{error}`));
    }

      return(

        <Fragment>
          <Row>
          {orders.filter(function(order) {
            if (!((JSON.parse(localStorage.getItem("res"))["restaurantName"]) === (order.restaurant_name))) {
              return false;
            }
            return true;
          }).map(function(dish){
            return(<Col sm={12} md={6} lg={4} key={dish.id}>
              <CustomerDish dish={dish} />
            </Col>
          )
          })}
          </Row>
        </Fragment>

     )
    }



export default Orders;
