import React,{useState} from "react";
import Axios from "axios";


function Restaurant({restaurant}){

  // if((restaurant.location).includes(JSON.parse(localStorage.getItem("user"))[0]["city"])){
  const[res,setRes]=useState("");

  const[fav,setFav]=useState("white");

function addToFavorites () {
  setFav("red");
}


  function getRestaurantLanding(){
    Axios.get("http://localhost:3001/Restaurant")
    .then((response) => {
          setRes(response.data.details[(restaurant.id)-1]);
          localStorage.setItem("res", JSON.stringify(response.data.details[(restaurant.id)-1]));
          window.open("/CustomerRestaurant","_self");
    })


  };

  return(
      <div className="card restaurant-style" style={{width:"66%"}}>
        <img src={`data:image/jpeg;base64,${restaurant.picture}`} className="card-img-top" alt="dish-img" style={{width:"300px",height:"150px"}} />
        <button type="button" style={{width:"30px",backgroundColor:"white",border:"none"}} onClick={addToFavorites} onChange={
          (e)=>{
            setFav(e.target.value);
          }
        }>
        <svg aria-hidden="true" focusable="false" data-prefix="far" data-icon="heart" class="svg-inline--fa fa-heart fa-w-16" role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"><path fill="currentColor" d="M458.4 64.3C400.6 15.7 311.3 23 256 79.3 200.7 23 111.4 15.6 53.6 64.3-21.6 127.6-10.6 230.8 43 285.5l175.4 178.7c10 10.2 23.4 15.9 37.6 15.9 14.3 0 27.6-5.6 37.6-15.8L469 285.6c53.5-54.7 64.7-157.9-10.6-221.3zm-23.6 187.5L259.4 430.5c-2.4 2.4-4.4 2.4-6.8 0L77.2 251.8c-36.5-37.2-43.9-107.6 7.3-150.7 38.9-32.7 98.9-27.8 136.5 10.5l35 35.7 35-35.7c37.8-38.5 97.8-43.2 136.5-10.6 51.1 43.1 43.5 113.9 7.3 150.8z"></path></svg>
        </button>
          <div className="card-body">
            <a href="#" onClick={getRestaurantLanding} style={{color:"green"}}><h5 className="card-title">{`${restaurant.restaurantName}`}</h5></a>
          </div>
      </div>
    )
  }
//   return(null)
// }


export default Restaurant;
