import React, {Fragment,useEffect,useState} from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Axios from "axios";
import {Row,Col} from "react-bootstrap";
import Favorite from "./Favorite.js";


function Favorites() {
  // constructor(props){
  //   super(props);
  //   this.state={
  //     menu:[]
  //   }
  // }
  //
  // componentDidMount(){
  //   this.fetchData();
  // }

    const [favs,getFavs]=useState([]);

    useEffect(() => {
      getFavorites();
    },[]);
    const name=JSON.parse(localStorage["user"])[0].name;
    const getFavorites = () => {
      // Axios.get("http://localhost:3001/Dishes.jsx")
      // .then(response => console.log(response))
      // .catch(err => console.log("failed:",err));
        Axios.get("http://localhost:3001/Favorites")
        .then((response) => {
          const allFav=response.data.details;
            getFavs(allFav);
        })
        .catch(error =>
          console.error(`Error:${error}`));
    }

    const [restaurants,getRestuarants]=useState([]);

    useEffect(() => {
      getAllRestaurants();
    },[]);

  const getAllRestaurants = () =>{
    Axios.get("http://localhost:3001/RestaurantDisplay")
    .then((response) => {
      const AllRestuarntData=response.data.details;
      getRestuarants(AllRestuarntData);
    })
    .catch(error =>
      console.error(`Error:{error}`));
  }

      return(
        <Fragment>
         <Row>
         <hr />
         <h3 style={{textAlign:"center", fontFamily:"Postmates", height:"40px",lineHeight:"50px"}}>Favorites</h3>
         <hr />
         {restaurants.filter(function(restaurant) {
           if ((restaurant.restaurantName).includes(JSON.parse(localStorage.getItem("user"))[0]["city"])) {
             return false;
           }
           return true;
         }).map(function(restaurant){
           return(<Col sm={12} md={6} lg={4} key={restaurant.id}>
             <Favorite restaurant={restaurant} />
           </Col>
         )
         })}
        </Row>
      </Fragment>
    );
    }



export default Favorites;
