import React,{useState,Fragment,useEffect} from "react";
import {Row,Col} from "react-bootstrap";
import Axios from "axios"
import SearchBar from "../CustomComponents/SearchBar.jsx"
import Restaurant from "./Restaurant.js";
import Navbar from "./Navbar"

function SearchResults(){

const[searchTerm,setSearchTerm]=useState("");
const [restaurants,getRestuarants]=useState([]);

useEffect(() => {
  getAllRestaurants();
},[]);

const getAllRestaurants = () =>{
  Axios.get("http://localhost:3001/RestaurantDisplay")
  .then((response) => {
    const AllRestuarntData=response.data.details;
    getRestuarants(AllRestuarntData);
  })
  .catch(error =>
    console.error(`Error:{error}`));
}

  return(
    <Fragment>
    <Navbar />
   <Row>
   <h3 style={{padding:"3%"}}>Search results:</h3>
   {restaurants.filter(function(restaurant) {
     if(searchTerm===""){
       return false;
     }
     else if(((restaurant.location).includes(searchTerm)) || ((restaurant.description).includes(searchTerm))) {
     return true;
   }}).map(function(restaurant){
     return(<Col sm={8} md={4} lg={3} key={restaurant.id}>
       <Restaurant restaurant={restaurant} />
     </Col>
   )
   })}
   </Row>
   </Fragment>
  )
}

export default SearchResults;
