import React,{useState} from "react";



  const[newDishName,setNewDishName]= useState("");
  const [newDishIngredients,setNewDishIngredients] = useState("");
  const [newDishPrice,setNewDishPrice] = useState("");
  const [newDishDescription,setNewDishDescription] = useState("");
  const [newDishCategory,setNewDishCategory] = useState("");
  const [newDishRes,setNewDishRes]=useState("");

  const editDish = (id) => {
    Axios.put("http://localhost:3001/EditDishes"),
    {
      newDishName:newDishName,
      newDishIngredients:newDishIngredients,
      newDishPrice:newDishPrice,
      newDishDescription:newDishDescription,
      newDishCategory:newDishCategory,
      newDishRes:newDishRes
    }
  }

function EditDishes(){
  return(
    <div className="card">
    <NavBar />
    <div className="card-body">
    <h2>{JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}</h2>
    </div>
    <div className="card-body">
    <input
     type="text"
     className = "form-control"
     name="newDishName"
     placeholder="Name of the Dish"
     onChange = {
       (e) => {
         setNewDishName(e.target.value);
       }
     }
    />
    </div>
    <div className="card-body">
    <input
     type="text"
    className = "form-control"
     name="newDishIngredients"
     placeholder="Ingredients"
     onChange = {
       (e) => {
         setNewDishIngredients(e.target.value);
       }
     }
    />
    </div>
    <div className="card-body">
    <input
     type="file"
    className = "form-control"
     name="newDishPrice"
     placeholder="Image"
     onChange = {
       (e) => {
         setNewDishPrice(e.target.value);
       }
     }
    />
    </div>
    <div className="card-body file">
    <input
    type="text"
   className = "form-control"
    name="newDishDescription"
    placeholder="Price of the Dish"
    onChange= { e => {
      setNewDishDescription(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    type="text"
    className = "form-control"
    name="newDishDescription"
    placeholder="Description of the food"
    onChange= { e => {
      setDishDescription(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    type="text"
    className = "form-control"
    name="newDishCategory"
    placeholder="Category of food"
    onChange= { e => {
      setNewDishCategory(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    type="text"
    className = "form-control"
    name="newDishRes"
    placeholder="Restaurant Name"
    onChange= { e => {
      setNewDishRes(e.target.value);
    }}
    />
    </div>
    <button type="button" class="btn btn-outline-light" onClick={()=>{editDishes(id)}}>Save Changes</button>
    </div>
  )
}


export default EditDishes;
