      <Col>
      <input
      id="res_location"
       type="text"
       name="resLocation"
       placeholder="Restaurant"
       className="form-control"
       defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
       onChange = {
         (e) => {
           setNewName(e.target.value);
         }
       }
      />
      </Col>
      <Col>
        <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}}>✔️</button>
        </Col>
        </Row>

        <Row style={{margin:"100px"}}>
        <Col>
        <input
        id="res_location"
         type="text"
         name="resLocation"
         placeholder="Restaurant"
         className="form-control"
         defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
         onChange = {
           (e) => {
             setNewName(e.target.value);
           }
         }
        />
        </Col>
        <Col>
          <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}}>✔️</button>
          </Col>
          </Row>

          <Row style={{margin:"100px"}}>
          <Col>
          <input
          id="res_location"
           type="text"
           name="resLocation"
           placeholder="Restaurant"
           className="form-control"
           defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
           onChange = {
             (e) => {
               setNewName(e.target.value);
             }
           }
          />
          </Col>
          <Col>
            <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}}>✔️</button>
            </Col>
            </Row>

            <Row style={{margin:"100px"}}>
            <Col>
            <input
            id="res_location"
             type="text"
             name="resLocation"
             placeholder="Restaurant"
             className="form-control"
             defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
             onChange = {
               (e) => {
                 setNewName(e.target.value);
               }
             }
            />
            </Col>
