import React,{useState} from "react";
import Navbar from "./Navbar.jsx";
import Calendar from "react-calendar";
import CustomerProfile from "./CustomerProfile.jsx"
import Select from 'react-select'
import countryList from 'react-select-country-list'

function EditCustomerProfile() {


  const[customerName,setCustomerName]= useState("");
  const [dob,setDob] = useState("");
  const [city,setCity] = useState("");
  const [country,setCountry] = useState("");
  const [newDishCategory,setNewDishCategory] = useState("");

  const changeHandler = (e) =>{
    setCountry(e.target.value);
  }


  return (
    <div className="card">
    <Navbar />
    <div className="card-body">
    <input
     type="text"
     name="customerName"
     placeholder="Name"
     defaultValue={JSON.parse(localStorage.getItem("user"))[0]["name"]}
     onChange = {
       (e) => {
         setCustomerName(e.target.value);
       }
     }
    />
    </div>
    <div className="card-body">
    <Calendar
     value={dob}
     activeStartDate={CustomerProfile.DOB}
     onChange = {
       (e) => {
         setDob(e.target.value);
       }
     }
    />
    </div>
    <div className="card-body">
    <input
     type="text"
    className = "form-control"
     name="city"
     placeholder="City"
     onChange = {
       (e) => {
         setCity(e.target.value);
       }
     }
    />
    </div>
    <div className="card-body">
    <Select
    options={options}
    value={country}
    onChange={changeHandler}
    />
    </div>
    <div className="card-body">
    <input
    type="text"
    className = "form-control"
    name="resDescription"
    placeholder="Restaurant Description"
    onChange= { e => {
      setNewDescription(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    type="text"
      className = "form-control"
    name="resContact"
    placeholder="Restaurant Contact"
    onChange= { e => {
      setNewContact(e.target.value);
    }}
    />
    </div>
    <div className="card-body">
    <input
    type="text"
      className = "form-control"
    name="resTimings"
    placeholder="Restaurant Timings"
    onChange= { e => {
      setNewTimings(e.target.value);
    }}
    />
    </div>
    <button type="button" class="btn btn-outline-light" onClick={showInput}>Save Changes</button>
    <center>
    <div className="container-fluid">
    <p><span id="name"></span></p>
    <p><span id="location"></span></p>
    <p><span id="image"></span></p>
    <p><span id="description"></span></p>
    <p><span id="contact"></span></p>
    <p><span id="timings"></span></p>
  </div>
  </center>
  </div>
  )
}

export default EditCustomerProfile;
