      {
        resName:JSON.parse(localStorage.getItem("user"))[0]["restaurantName"],
        newName:newName
      })
      alert("Updated successfully")
    }


  function editDishIngredients() {
      Axios.put("http://localhost:3001/EditName",
      {
        resName:JSON.parse(localStorage.getItem("user"))[0]["restaurantName"],
        newName:newName
      })
      alert("Updated successfully")
    }
  function editDishPrice() {
      Axios.put("http://localhost:3001/EditLocation",
      {
        resName:JSON.parse(localStorage.getItem("user"))[0]["restaurantName"],
        newLocation:newLocation,
      })
      alert("Updated successfully")
    }
  function editDishDescription() {
        Axios.put("http://localhost:3001/EditImg",
        {
          resName:JSON.parse(localStorage.getItem("user"))[0]["restaurantName"],
          newImg:newImg
        })
        alert("Updated successfully")
      }
  function editDishCategory() {
        Axios.put("http://localhost:3001/EditDescription",
        {
        resName:JSON.parse(localStorage.getItem("user"))[0]["restaurantName"],
        newDescription:newDescription
        })
        alert("Updated successfully")
      }

  return(
    <Fragment>
    <NavBar />
    <h2>{JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}</h2>
    <Row style={{margin:"100px"}}>
    <Col>
    <input
     type="text"
     name="newName"
     placeholder="Restaurant"
     className="form-control"
     defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
     onChange = {
       (e) => {
         setNewName(e.target.value);
       }
     }
    />
    </Col>
    <Col>
      <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}} onClick={()=>{editDishName(dish.name)}}>✔️</button>
      </Col>
      </Row>
    <Row style={{margin:"100px"}}>
    <Col>
    <input
     type="text"
     name="newName"
     placeholder="Restaurant"
     className="form-control"
     defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
     onChange = {
       (e) => {
         setNewName(e.target.value);
       }
     }
    />
    </Col>
    <Col>
      <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}} onClick={()=>{editDishIngredients(dish.name)}}>✔️</button>
      </Col>
      </Row>

      <Row style={{margin:"100px"}}>
      <Col>
      <input
       type="text"
       name="newDishIngredients"
       placeholder="Description"
       className="form-control"
       onChange = {
         (e) => {
           setNewDishIngredients(e.target.value);
         }
       }
      />
      </Col>
      <Col>
        <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}} onClick={()=>{editDishPrice(dish.name)}}>✔️</button>
        </Col>
        </Row>

        <Row style={{margin:"100px"}}>
        <Col>
        <input
         type="text"
         name="newDishPrice"
         placeholder="Price"
         className="form-control"
         onChange = {
           (e) => {
             setNewDishPrice(e.target.value);
           }
         }
        />
        </Col>
        <Col>
          <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}} onClick={()=>{editDishDescription(dish.name)}}>✔️</button>
          </Col>
          </Row>

          <Row style={{margin:"100px"}}>
          <Col>
          <input
           type="text"
           name="newDescription"
           placeholder="Description"
           defaultValue={JSON.parse(localStorage.getItem("user"))[0]["restaurantName"]}
           className="form-control"
           onChange = {
             (e) => {
               setNewDescription(e.target.value);
             }
           }
          />
          </Col>
          <Col>
            <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}} onClick={()=>{editDishCategory(dish.name)}}>✔️</button>
            </Col>
            </Row>

            <Row style={{margin:"100px"}}>
            <Col>
            <input
             type="text"
             name="newDishCategory"
             placeholder="Category"
             className="form-control"
             onChange = {
               (e) => {
                 setNewDishCategory(e.target.value);
               }
             }
            />
            </Col>
            <Col>
              <button type="button" style={{borderRadius:"100%",backgroundColor:"green",width:"30px",height:"20px"}} onClick={()=>{editDishIngredients(dish.name)}}>✔️</button>
              </Col>
              </Row>

    </Fragment>
  );

}

export default Edit;
