import React, {useState} from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Axios from "axios";
import PropTypes from 'prop-types';

function RestaurantLogin({setToken}) {

    const[email,setEmail] = useState("");
    const[password,setPassword] = useState("");

    const[restaurantStatus,setRestaurantStatus]=useState("");

    const restaurantLogin = (e) => {
      e.preventDefault();
      Axios.post("http://localhost:3001/RestaurantLogin.jsx",
      {
        email:email,
        password:password
      }).then((response) => {
        if(response.data.message){
          setRestaurantStatus(response.data.message)
        }
        else{
          setRestaurantStatus("Logged In successfully!");
          setToken(token);
          window.open("./RestaurantLanding.jsx","_self");
        }

      });
    };

      // async function loginUser(credentials) {
      // return fetch('http://localhost:8080/login', {
      //   method: 'POST',
