  //   e.preventDefault();
  //   const token = await loginUser({
  //     email,
  //     password
  //   });
  //   setToken(token);
  // }

  return (
      <
      div >
      <
      nav className = "navbar navbar-expand-lg navbar-light bg-light" >
      <
      div className = "container-fluid" >
      <
      a className = "navbar-brand"
      href = "http://localhost:3000/CustomerLogin.jsx" > < p className = "login" > Customer ? SignIn < /p>< /a > <
      button className = "navbar-toggler"
      type = "button"
      data-bs-toggle = "collapse"
      data-bs-target = "#navbarNavDropdown"
      aria-controls = "navbarNavDropdown"
      aria-expanded = "false"
      aria-label = "Toggle navigation" >
      <
      span className = "navbar-toggler-icon" > < /span> < /
      button > <
      div className = "collapse navbar-collapse"
      id = "navbarNavDropdown" >
      <
      ul className = "navbar-nav" >
      <
      li className = "nav-item" >
      <
      a className = "nav-link active"
      aria-current = "page"
      href = "http://localhost:3000/" > Home < /a> < /
      li > <
      li className = "nav-item" >
      <
      a className = "nav-link"
      href = "http://localhost:3000/CustomerSignup" > Customer SignUp < /a> < /
      li > <
      li className = "nav-item" >
      <
      a className = "nav-link"
      href = "http://localhost:3000/RestaurantSignup" > Restaurant SignUp < /a> < /
      li > <
      li className = "nav-item dropdown" >
      <
      /li> < /
      ul > <
      /div> < /
      div > <
      /nav> <
      h1 className = "customer-heading" > Uber < span className = "heading-eats" > Eats < /span></h1 >
      <
      h1 className = "form-floating" > Welcome Back! < /h1> <
      div className = "form-floating" >
      <
      input type = "email"
      className = "form-control"
      id = "floatingInput"
      placeholder = "name@example.com"
      name = "email"
      onChange = {
        (e) => {
          setEmail(e.target.value);
        }
      }
      / > <
      label
      htmlFor = "floatingInput" > Sign in with your email address < /label> < /
      div > <
      div className = "form-floating mb-3" >
      <
      input type = "password"
      className = "form-control"
      id = "floatingPassword"
      placeholder = "Password"
      name = "password"
      onChange = {
        (e) => {
          setPassword(e.target.value);
        }
      }
      / > <
      label
      htmlFor = "floatingPassword" > Password < /label> < /
      div > <
      div className = "d-grid gap-2 form-floating" >
      <button onClick={restaurantLogin} type = "button"
      className = "btn btn-outline-primary" > Continue< /button>
      < /
      div >
      <center>
        <h4 className="err-msg">{restaurantStatus}</h4>
      </center>
      <
      /div>
    );
    }

    RestaurantLogin.propTypes = {
    setToken: PropTypes.func.isRequired
    }

export default RestaurantLogin;
